export function limparFormularioCep() {
    document.getElementById('rua').value = "";
    document.getElementById('bairro').value = "";
    document.getElementById('cidade').value = "";
    document.getElementById('uf').value = "";
  }
  
  export function meuCallback(conteudo) {
    if (!("erro" in conteudo)) {
      document.getElementById('rua').value = conteudo.logradouro;
      document.getElementById('bairro').value = conteudo.bairro;
      document.getElementById('cidade').value = conteudo.localidade;
      document.getElementById('uf').value = conteudo.uf;
    } else {
      limparFormularioCep();
      alert("CEP não encontrado.");
    }
  }
  
  export function pesquisarCep(cep) {
    const cepSemFormato = cep.replace(/\D/g, '');
  
    if (cepSemFormato !== "") {
      const validacep = /^[0-9]{8}$/;
  
      if (validacep.test(cepSemFormato)) {
        document.getElementById('rua').value = "...";
        document.getElementById('bairro').value = "...";
        document.getElementById('cidade').value = "...";
        document.getElementById('uf').value = "...";
  
        const script = document.createElement('script');
        script.src = 'https://viacep.com.br/ws/' + cepSemFormato + '/json/?callback=meuCallback';
        document.body.appendChild(script);
      } else {
        limparFormularioCep();
        alert("Formato de CEP inválido.");
      }
    } else {
      limparFormularioCep();
    }
  }
